import React, {useContext,useState} from 'react'
import { UserContext } from '../usercontext/usercontext'
import { Form, Button } from 'react-bootstrap';
const Create = () => {
    const [users, setUsers]=useContext(UserContext);
    
    const[id, setId]=useState("");
    const[name, setName]=useState("");
    const[address, setAddress]=useState("");
    const[qualification, setQualification]=useState("");
    const[mobile, setMobile]=useState("");

    const handleSubmit=(e)=>{
        e.preventDefault();
        setName(name)
        console.log(name);
    }
  return (
    <div style={{width:"60%", margin:"5rem auto"}}>
      <Form onSubmit={handleSubmit()}>
      <Form.Group className="mb-3">
            <Form.Label>ID</Form.Label>
            <Form.Control type="text" name="id" value={id} placeholder="Enter ID" onChange={(e)=>e.target.value} />
        </Form.Group>
        <Form.Group className="mb-3">
            <Form.Label>Name</Form.Label>
            <Form.Control type="text" name="name" value={name} placeholder="Enter Name" onChange={(e)=>e.target.value} />
        </Form.Group>
        <Form.Group className="mb-3">
            <Form.Label>Address</Form.Label>
            <Form.Control type="text" name="address" value={address} placeholder="Enter Address" onChange={(e)=>e.target.value}/>
        </Form.Group>
        <Form.Group className="mb-3">
            <Form.Label>Qualification</Form.Label>
            <Form.Control type="text" name="qualification" value={qualification} placeholder="Enter Qualification" onChange={(e)=>e.target.value}/>
        </Form.Group>
        <Form.Group className="mb-3">
            <Form.Label>Mobile No.</Form.Label>
            <Form.Control type="text" name="mobile" value={mobile} placeholder="Enter Moible" onChange={(e)=>e.target.value}/>
        </Form.Group>
    
            <Button variant="primary" type="submit">
                Submit
            </Button>
     </Form>
    </div>
  )
}

export default Create
