import React, {useContext,useState} from 'react'
import { UserContext } from '../usercontext/usercontext'
import { Form, Button } from 'react-bootstrap';
const Create = () => {
    

    const[id, setId]=useState("");
    const[name, setName]=useState("");
    const[address, setAddress]=useState("");
    const[qualification, setQualification]=useState("");
    const[mobile, setMobile]=useState("");
    const updateId=(e)=>{
        setId(e.target.value);
    }
    const updateName=(e)=>{
        setName(e.target.value);
    }
    const updateAddress=(e)=>{
        setAddress(e.target.value);
    }
    const updateQualification=(e)=>{
        setQualification(e.target.value);
    }
    const updateMobile=(e)=>{
        setMobile(e.target.value);
    }

    const handleSubmit=(e)=>{
        e.preventDefault();
        console.log(name);
    }
  return (
    <div style={{width:"60%", margin:"5rem auto"}}>
      <Form onSubmit={handleSubmit()}>
      <Form.Group className="mb-3">
            <Form.Label>ID</Form.Label>
            <Form.Control type="text" name="id" value={id} placeholder="Enter ID" onChange={updateId} />
        </Form.Group>
        <Form.Group className="mb-3">
            <Form.Label>Name</Form.Label>
            <Form.Control type="text" name="name" value={name} placeholder="Enter Name" onChange={updateName} />
        </Form.Group>
        <Form.Group className="mb-3">
            <Form.Label>Address</Form.Label>
            <Form.Control type="text" name="address" value={address} placeholder="Enter Address" onChange={updateAddress}/>
        </Form.Group>
        <Form.Group className="mb-3">
            <Form.Label>Qualification</Form.Label>
            <Form.Control type="text" name="qualification" value={qualification} placeholder="Enter Qualification" onChange={updateQualification}/>
        </Form.Group>
        <Form.Group className="mb-3">
            <Form.Label>Mobile No.</Form.Label>
            <Form.Control type="text" name="mobile" value={mobile} placeholder="Enter Moible" onChange={updateMobile}/>
        </Form.Group>
    
            <Button variant="primary" type="submit">
                Submit
            </Button>
     </Form>
    </div>
  )
}

export default Create
